clc clearfunction y = fa(x)  y = sin(x);    endfunctionfunction y = fb(x)  y = cos(x);    endfunctionfunction y = fc(x)  y = sqrt(1 .+ exp(0.5 .* x .^ 2));    endfunctionfunction y = fd(x)  y = tan(x);    endfunctionfunction y = fe(x)  y = (1 .- exp(-x))./x;    endfunctionfunction y = ff(x)  y = sqrt(1 .+ 2 .* x .^ 4);    endfunctionfunction y = fg(x)  y = x .^ 3 ./ (exp(x).- 1);    endfunctionfunction y = fh(x)  y = exp(x) ./ x .^ 2;    endfunction#Ein paar coole Funktion integrieren disp("1")disp("Trapezregel: ")x = 0:0.1:3.14; area = trapz(x, fa(x))disp("Simpson-Regel: ")area = quad('fa', 0, 3.14)disp("Monte-Carlo: ")area = mcintgr("fa", 0, 3.14, 10000)disp("2")disp("Trapezregel: ")x = 0:0.1:3.14/2; area = trapz(x, fb(x))disp("Simpson-Regel: ")area = quad('fb', 0, 3.14/2)disp("Monte-Carlo: ")area = mcintgr("fb", 0, 3.14/2, 10000)disp("3")disp("Trapezregel: ")x = 1:0.1:2.6; area = trapz(x, fc(x))disp("Simpson-Regel: ")area = quad('fc', 1, 2.6)disp("Monte-Carlo: ")area = mcintgr("fc", 1, 2.6, 10000)disp("4")disp("Trapezregel: ")x = -1:0.1:0; area = trapz(x, fd(x))disp("Simpson-Regel: ")area = quad('fd', -1,0)%%Monte Carlos l√§uft nicht, weil y Werte kleiner 0 sind%disp("Monte-Carlo: ")%area = mcintgr("fd", -1, 0, 10000)disp("5")disp("Trapezregel: ")x = 1:0.1:2; area = trapz(x, fe(x))disp("Simpson-Regel: ")area = quad('fe', 1,2)disp("Monte-Carlo: ")area = mcintgr("fe", 1, 2, 10000)disp("6")disp("Trapezregel: ")x = 1:0.1:4; area = trapz(x, ff(x))disp("Simpson-Regel: ")area = quad('ff', 1,4)disp("Monte-Carlo: ")area = mcintgr("ff", 1, 4, 10000)disp("7")disp("Trapezregel: ")x = 0.5:0.1:1; area = trapz(x, fg(x))disp("Simpson-Regel: ")area = quad('fg', 0.5,1)disp("Monte-Carlo: ")area = mcintgr("fg", 0.5, 1, 10000)disp("8")disp("Trapezregel: ")x = 1:0.1:3; area = trapz(x, fh(x))disp("Simpson-Regel: ")area = quad('fh', 1,3)disp("Monte-Carlo: ")area = mcintgr("fh", 1, 3, 10000)